package ControlStatements;


// this program is to find which is greater no between two no

/*
 * sddfsf
 * sdfsddf
 * sddfdsa
 */
public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 4, y = 5;
		
		if(x > y)  // < <= > >= == !=
			{
			System.out.println(x + " is greater than " + y);  // 6 is greater than 5
			}
		else
			System.out.println(y + " is greater than " + x);
	}
}
